import React, { useEffect, useState } from "react";
import userApi from "../../../api/api-user-setting";

export default function Setting() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    userAll();
  }, []);

  const userAll = async () => {
    await userApi.findOneUser(1).then((data) => {
      setUsers(data);
    });
  };
  console.info(users);
  return (
    <div className="">
      <div className="border border-slate-300 pl-6 border-b-slate-600 ">
        {/* Logo */}
        <div className="mt-6 flex border-b-2">
          <span className="border border-slate-500 p-5 rounded-full mr-5 mb-2"></span>
          <p className="text-2xl mt-2 font-semibold mb-2">Code Academy</p>
        </div>
        <div>
          <p className="my-4">Home / Setting</p>
        </div>
      </div>
      {/* setting */}
      <div className="border border-slate-300">
        <h1 className="text-lg font-semibold my-2 pl-4">Setting</h1>
      </div>
      {/* container profile */}
      <div className="border border-slate-300 h-[100vh]">
        {/* countainer rounded */}
        <div className="border border-slate-800 mx-2 mt-2 rounded-t-2xl h-[100vh]">
          <div className="">
            <p className="border border-slate-500 mt-5 p-1 pl-2 font-semibold">
              Profile
            </p>
          </div>
          <div className="my-4">
            <p className="pl-6">
              This information will be display, so be careful what you share
            </p>
            {/* Foto dan text */}
            <div className="my-7 flex">
              <span className="border border-slate-500 p-9 rounded-full ml-8"></span>
              <div className="ml-6 ">
                <p>Kang Dian</p>
                <p>Candidat Botcamp</p>
              </div>
            </div>
            <div className="flex justify-end mr-6">
              <button>Edit</button>
            </div>
          </div>
          {/* end Foto dan text */}
          {/* Login */}
          <div>
            <p className="border border-slate-500 mt-5 p-1 pl-2 font-semibold">
              Login
            </p>
            <div className="flex justify-between">
              <p className="pl-6 mt-4">Change Password</p>
              <button className="mx-6 mt-4 ">Edit</button>
            </div>
          </div>
          {/* end Login */}
          {/* Container Email */}
          <div>
            <div>
              <p className="border border-slate-500 mt-5 p-1 pl-2 font-semibold">
                Emails
              </p>
            </div>
            <div className="flex">
              <p className="pl-6 mt-4 mr-5">Your Emails :</p>
              <div>
                <p className="pl-6 mt-4 border-b-2 w-full">Your Emails :</p>
                <p className="pl-6 mt-4">Your Emails :</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
