import axios from "axios";
import config from "../config/config";

const findOneUser = async (id) => {
  try {
    const result = await axios.get(`${config.domain}/setting/${id}`);
    return result.data;
  } catch (error) {
    return error.messagge;
  }
};

export default {
  findOneUser,
};
